INSERT INTO `tbl_products` VALUES (58,'Túi Đeo Chéo Da Nam Lata BALC27D',420000.00,NULL,'','<div class=\"panel-heading\" role=\"tab\" id=\"heading-product-description\" style=\"margin: 0px; padding: 15px; font-family: SVN_Gotham, sans-serif; border: 0px; border-top-left-radius: 3px; border-top-right-radius: 3px; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); font-size: 14px;\"><h4 class=\"panel-title\" style=\"margin-right: -15px; margin-bottom: 0px; margin-left: -15px; padding: 0px; font-family: inherit; line-height: 18px; color: inherit; font-size: 13px;\"><a class=\"text-uppercase\" role=\"button\" data-toggle=\"collapse\" data-parent=\"#accordion\" href=\"https://latashop.com/products/tui-deo-cheo-da-nam-lata-balc27d-mau-den#product-description\" aria-expanded=\"false\" aria-controls=\"product-description\" style=\"margin: 0px; padding: 0px; color: inherit; outline: none; transition: opacity 150ms linear 0s, color 150ms linear 0s, background 150ms linear 0s; line-height: 1.1; position: relative; display: block;\">MÔ TẢ SẢN PHẨM</a></h4></div><div id=\"product-description\" class=\"panel-collapse collapse in\" role=\"tabpanel\" aria-labelledby=\"heading-product-description\" style=\"margin: 0px; padding: 0px; font-family: SVN_Gotham, sans-serif; display: block; visibility: visible; color: rgb(37, 42, 43); font-size: 14px;\"><div class=\"panel-body\" style=\"margin: 0px; padding: 0px; border: 0px;\"><div class=\"description-content\" style=\"margin: 0px; padding: 0px; background: rgb(245, 245, 245);\"><div class=\"description-productdetail\" style=\"margin: 0px; padding: 0px; background: rgb(255, 255, 255); font-size: 13px;\"><div id=\"ega-uti-editable-content\" data-platform=\"haravan\" data-id=\"1030619366\" style=\"margin: 0px; padding: 0px;\"><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; line-height: 21px;\"><span style=\"margin: 0px; padding: 0px; font-weight: 700;\"><a href=\"https://latashop.com/collections/tui-deo-cheo-da-nam\" style=\"margin: 0px; padding: 0px; color: rgb(37, 42, 43); outline: none; transition: opacity 150ms linear 0s, color 150ms linear 0s, background 150ms linear 0s;\">Túi đeo chéo da nam Lata</a></span>&nbsp;BALC27 với kiểu dáng trẻ trung, thích hợp cho các bạn thời hiện đại. Với 4 gam màu trung tính bạn có thể lựa chọn. Bò đậm thanh lịch, bò nhạt trẻ trung, nâu mạnh mẽ và đen cá tính.</p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; line-height: 21px;\">THÔNG TIN KỸ THUẬT</p><hr style=\"margin: 0px 0px 10px; padding: 0px; width: 570px; border-top-color: rgb(174, 176, 176);\"><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; line-height: 21px;\"><span style=\"margin: 0px; padding: 0px; font-weight: 700;\">Kích thước</span>: Dài 26cm x rộng 6,5cm x cao 12cm</p><hr style=\"margin: 0px 0px 10px; padding: 0px; width: 570px; border-top-color: rgb(174, 176, 176);\"><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; line-height: 21px;\"><span style=\"margin: 0px; padding: 0px; font-weight: 700;\">Số ngăn</span>: 5 ngăn ( 1 ngăn chính, 4 ngăn phụ )</p><hr style=\"margin: 0px 0px 10px; padding: 0px; width: 570px; border-top-color: rgb(174, 176, 176);\"><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; line-height: 21px;\"><span style=\"margin: 0px; padding: 0px; font-weight: 700;\">Công dụng</span>&nbsp;:Đựng vừa sổ tay, điện thoại Iphone 11 ProMax, ví tiền, cáp sạc</p><hr style=\"margin: 0px 0px 10px; padding: 0px; width: 570px; border-top-color: rgb(174, 176, 176);\"><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; line-height: 21px;\"><span style=\"margin: 0px; padding: 0px; font-weight: 700;\">Trọng lượng</span>&nbsp;: 298 grams</p><hr style=\"margin: 0px 0px 10px; padding: 0px; width: 570px; border-top-color: rgb(174, 176, 176);\"><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; line-height: 21px;\"><span style=\"margin: 0px; padding: 0px; font-weight: 700;\">Chất liệu:</span>&nbsp;Da tổng hợp cao cấp (*)</p><hr style=\"margin: 0px 0px 10px; padding: 0px; width: 570px; border-top-color: rgb(174, 176, 176);\"><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; line-height: 21px;\"><span style=\"margin: 0px; padding: 0px; font-weight: 700;\">Xuất xứ:</span>&nbsp;Việt Nam</p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; line-height: 21px;\">(*) Chất liệu da tổng hợp cao cấp giúp sản phẩm luôn sáng bóng, chống thấm nước và bền bỉ, sử dụng lâu dài không bị bong tróc hay nổ da.</p></div></div></div></div></div>','product/avatar/tui-deo-cheo-1.JPG',17,'2021-03-12 15:53:00',NULL,NULL,NULL,1,'tui-eo-cheo-da-nam-lata-balc27d-1617645331706','Đen huyền bí','Da bê',100),(59,'Túi Đeo Chéo Da Nam Lata BALC27N',420000.00,NULL,'','<p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; font-family: SVN_Gotham, sans-serif; line-height: 21px; color: rgb(37, 42, 43); font-size: 13px; background-color: rgb(255, 255, 255);\"><span style=\"margin: 0px; padding: 0px; font-weight: 700;\"><a href=\"https://latashop.com/collections/tui-deo-cheo-da-nam\" style=\"margin: 0px; padding: 0px; color: rgb(37, 42, 43); outline: none; transition: opacity 150ms linear 0s, color 150ms linear 0s, background 150ms linear 0s;\">Túi đeo chéo da nam Lata</a></span>&nbsp;BALC27 với kiểu dáng trẻ trung, thích hợp cho các bạn thời hiện đại. Với 4 gam màu trung tính bạn có thể lựa chọn. Bò đậm thanh lịch, bò nhạt trẻ trung, nâu mạnh mẽ và đen cá tính.</p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; font-family: SVN_Gotham, sans-serif; line-height: 21px; color: rgb(37, 42, 43); font-size: 13px; background-color: rgb(255, 255, 255);\">THÔNG TIN KỸ THUẬT</p><hr style=\"margin: 0px 0px 10px; padding: 0px; font-family: SVN_Gotha